package WEEK6;

public class Day1NestedIF {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int a=1;
  int b=2;
  int c=3;
	if(a>b &&a>c) {
		System.out.println("a is Greater"+a);
	}
	else if (b>c&&b>a) {
		System.out.println("b is Greater"+b);
		
	}
	else {
		System.out.println("c is greater"+c);
	}
	}

}
